&quot; + &quot; &lt;td&gt;&lt;input type=\&quot;checkbox\&quot; class=\&quot;choose\&quot;&gt;&lt;/td&gt;
&quot; + &quot; &lt;td&gt;1&lt;/td&gt;
&quot; + &quot; &lt;td&gt;&quot; + ipt1 + &quot;&lt;/td&gt;
&quot; + &quot; &lt;td&gt;&quot; + ipt2 + &quot;&lt;/td&gt;
&quot; + &quot; &lt;td&gt;&quot; + ipt3 + &quot;&lt;/td&gt;
&quot; + &quot; &lt;td&gt;&quot; + ipt4 + &quot;&lt;/td&gt;
&quot; + &quot; &lt;td&gt;&quot; + ipt5 + &quot;&lt;/td&gt;
